{% set cust_address = frappe.db.sql(""" select ad.address_line1, ad.address_line2, ad.city, ad.state, ad.pincode, ad.phone, ad.gstin, ad.email_id from `tabAddress` as ad join `tabDynamic Link` as dl on dl.parent = ad.name where ad.address_type = 'Shipping' and dl.link_name = '{}' """.format(doc.customer)) %}
{% set cust_address_ = "" %}
{% set cust_phone = "" %}
{% set cust_gst = "" %}
{% set cust_email = "" %}
{% if cust_address %}
    {% set cust_address_ = cust_address[0][0] + ", " + cust_address[0][2] + ", " + cust_address[0][3] + ", " + cust_address[0][4] %}
    {% set cust_phone = cust_address[0][5] %}
    {% set cust_gst = cust_address[0][6] %}
    {% set cust_email = cust_address[0][7] %}
{% endif %}
<div id="main">
    <h3 style="text-align: center; margin-bottom: auto;">SALES ORDER</h3>
    <div id="main1">
        <h5 style="text-align: center; font-weight: bold; margin-top: auto;border-top: 1px solid black">{{ doc.company }}</h5>
    </div>
    <table style='width: 100%; font-size: inherit;'>
        <tr>
            <th style="font-weight:bold; width:15%; border: 1px solid black; color: black" rowspan="3">Details of Seller:</th>
            {% set company_doc = frappe.get_doc("Company", doc.company) %}
            <td style="border: 1px solid black; color: black">GSTIN No: {{ doc.company_gstin or "" }}</td>
            <td style="border: 1px solid black; color: black">CIN: {{ company_doc.custom_cin or "" }}</td>
            <td style="border: 1px solid black; color: black">IEC:</td>
            <td style="border: 1px solid black; color: black">PAN: {{ company_doc.pan or "" }}</td>
        </tr>
        <tr>
            <td style="border: 1px solid black; color: black">Billing Address</td>
            <td style="border: 1px solid black; color: black" colspan="3">{{ doc.address_display }}</td>
        </tr>
        <tr>
            <td style="border: 1px solid black; color: black">Phone No: {{ company_doc.phone_no or "" }}</td>
            <td style="border: 1px solid black; color: black">Tax No: {{ company_doc.tax_id or "" }}</td>
            <td style="border: 1px solid black; color: black" colspan="2">Email: {{ company_doc.email or "" }}</td>
        </tr>
    </table>

    <table style="width: 100%; font-size: inherit;">
        <tr>
            <td style="width: 50%; color: black; border-right: 1px solid black; "><b>Order Number:</b> {{ doc.name }}
                <br>
                {% set date = frappe.utils.get_datetime(doc.transaction_date) %}
                <b>Date:</b> {{ date.strftime('%d-%m-%y') if date else '' }}
                <br>
                <b>Customer Code:</b>
                <br>
                <b>Details of Customer</b><br>
                Customer Name:  {{ doc.customer_name }}
                <br>
                Customer Address: {{ cust_address_ }}
            </td>
            <td style="border-bottom: 1px solid black">
                <b>Billing Address</b>
                <br>Name: {{ doc.customer_address }}<br>
                Address: {{ doc.address_display }}
                GST:{{doc.company_gstin}}<br>
            </td>
        </tr>        
        <tr>
            <td style="width: 50%; color: black; border-right: 1px solid black"><b>Contact Person</b>
                <br>
                {% set email = frappe.db.get_value("Address", doc.customer_address,'email_id') %}
                {% set get_contact_details = frappe.db.sql(""" select ce.email_id from `tabDynamic Link` dl join `tabContact Email` ce on dl.parent = ce.parent where dl.link_name = '{}' """.format(doc.customer)) %}
                {% if get_contact_details %}
                    {% set email = get_contact_details[0][0] %}
                {% endif %}
                {% set number = "" %}
                {% set get_contact = frappe.db.sql(""" select cp.phone from `tabDynamic Link` dl join `tabContact Phone` cp on dl.parent = cp.parent where dl.link_name = '{}' """.format(doc.customer)) %}
                {% if get_contact %}
                    {% set number = get_contact[0][0] %}
                    {% set customer_pan = frappe.db.get_value("Customer", doc.customer, 'pan') %}
                {% endif %}
                Mobile No: {{ number or "" }}
                <br>
                PAN No: {{ customer_pan or ""}}
                <br>
                GSTIN: {{ cust_gst or "" }}
                <br>
                Email: {{ email or "" }}
            </td>
            <td>
                <b>Shipping Address</b>
                <br>
                {{ doc.shipping_address }}<br>
                GST:{{doc.company_gstin}}
            </td>
        </tr>
    </table>
    <table style="width: 100%; font-size: inherit;">
        <tr>
            <th style="width: 1%; color: black; border: 1px solid black">Sr.No</th>
            <th style="width: 9%; color: black; border: 1px solid black">Item No</th>
            <th style="width: 10%; color: black; border: 1px solid black">Description</th>
            <th style="width: 10%; color: black; border: 1px solid black">HSN Code</th>
            <th style="width: 10%; color: black; border: 1px solid black">Qty</th>
            <th style="width: 10%; color: black; border: 1px solid black">UOM</th>
            <th style="width: 10%; color: black; border: 1px solid black">Rate</th>
            <th style="width: 10%; color: black; border: 1px solid black">Disc</th>
            <th style="width: 6%; color: black; border: 1px solid black">GST Percentage (%)</th>
            <th style="width: 6%; color: black; border: 1px solid black">GST Amount</th>
            <th style="width: 14%; color: black; border: 1px solid black">Amount</th>
        </tr>
        {% set ns = namespace(total = 0) %}
        {% set gs = namespace(gst = 0) %}
        {% for i in doc.items %}
            <tr>
                <td style="color: black; border: 1px solid black">{{ i.idx }}</td>
                <td style="color: black; border: 1px solid black">{{ i.item_code }}</td>
                <td style="color: black; border: 1px solid black">{{ i.description }}</td>
                {% set hsn = frappe.db.get_value("Item", i.item_code, 'gst_hsn_code') %}
                <td style="color: black; border: 1px solid black">{{ hsn or "" }}</td>
                <td style="color: black; border: 1px solid black">{{ i.qty }}</td>
                <td style="color: black; border: 1px solid black">{{ i.uom }}</td>
                <td style="color: black; border: 1px solid black">{{ i.rate }}</td>
                {% if i.discount_amount > 0 %}
                    <td style="color: black; border: 1px solid black">{{ i.discount_amount }}</td>
                {% else %}
                    <td style="color: black; border: 1px solid black">0</td>
                {% endif %}
                {% set gs.gst = gs.gst + (i.cgst_amount + i.sgst_amount + i.igst_amount)  %}
                {% set gst_per = namespace(total = 0) %}
                {% set template_tax = frappe.db.get_value("Item Tax Template", i.item_tax_template, 'gst_rate') %}
                <td style="color: black; border: 1px solid black">{{ template_tax }}</td>
                {% set gst_amount = (i.amount * (template_tax / 100)) %}
                {% set rounded_gst_amount = gst_amount | round(2) %}
                <td style="color: black; border: 1px solid black">{{ rounded_gst_amount }}</td>
                {% if i.discount_amount > 0 %}
                    {% set ns.total = ns.total + (i.amount+i.cgst_amount+i.sgst_amount+i.igst_amount)-i.discount_amount %}
                {% else %}
                    {% set ns.total = ns.total + (i.amount+i.cgst_amount+i.sgst_amount+i.igst_amount) %}
                {% endif %}
                {% if i.discount_amount > 0 %}
                    <td style="color: black; border: 1px solid black">{{ i.amount-i.discount_amount }}</td>
                {% else %}
                    <td style="color: black; border: 1px solid black">{{ i.amount }}</td>
                {% endif %}
            </tr>
        {% endfor %}
    </table>
    <table style="width: 100%; font-size: inherit;">
        <tr>
            <td id="" style="width: 77%; text-align: right; color: black; padding-top: 4px !important; padding-bottom: 4px !important;">Total</td>
            <td id="" style="width: 23%; color: black; border-left: 1px solid black; padding-top: 4px !important; padding-bottom: 4px !important;"> <b>{{ doc.net_total}}/-</b></td>
        </tr>
        <tr>
            <td id="" style="width: 77%; text-align: right; color: black; padding-top: 4px !important; padding-bottom: 4px !important;">Total Qty</td>
            <td id="" style="width: 23%; color: black; border-left: 1px solid black; padding-top: 4px !important; padding-bottom: 4px !important;"> <b>{{ doc.total_qty}}/-</b></td>
        </tr>
        {% for i in doc.taxes %}
            <tr>
                <td id="" style="width: 77%; text-align: right; color: black; padding-top: 4px !important; padding-bottom: 4px !important;">  {{ i.description }}</td>
                <td id="" style="width: 23%; color: black; border-left: 1px solid black; padding-top: 4px !important; padding-bottom: 4px !important;"> <b>{{ i.tax_amount }}/-</b> </td>
            </tr>
        {% endfor %}
        <tr>
            <td id="" style="width: 77%; text-align: right; color: black; padding-top: 4px !important; padding-bottom: 4px !important;">Rounded Total</td>
            <td id="" style="width: 23%; color: black; border-left: 1px solid black; padding-top: 4px !important; padding-bottom: 4px !important;"> <b>{{ doc.rounded_total }}/-</b></td>
        </tr>
        <tr>
            <td id="" style="width: 77%; text-align: right; color: black; padding-top: 4px !important; padding-bottom: 4px !important;">Rounded Value</td>
            <td id="" style="width: 23%; color: black; border-left: 1px solid black; padding-top: 4px !important; padding-bottom: 4px !important;"> <b>{{ (doc.grand_total - doc.rounded_total) | round(2) }}/-</b></td>
        </tr>
        <tr>
            <td id="" style="width: 77%; text-align: right; color: black; padding-top: 4px !important; padding-bottom: 4px !important;">Grand Total</td>
            <td id="" style="width: 23%; color: black; border-left: 1px solid black; padding-top: 4px !important; padding-bottom: 4px !important;"> <b>{{ doc.grand_total }}</b>/-</td>
        </tr>
    </table>
    <h6 style="text-align: left; margin-top: auto;border-top: 1px solid black">In Words: <b>{{ doc.in_words or "" }}</b></h6>
    <h6 style="text-align: left; margin-top: auto;border-top: 1px solid black">Payment Terms: {{ doc.payment_terms_template or "" }}</h6>
    <h5 style="text-align: left; font-weight: bold; margin-top: auto;border-top: 1px solid black">Terms & Conditions</h5>
    {{ doc.terms or "" }}
    <br>
    <br>
    <br>
    <br>
</div>

